(ns chaos.gts)
(use 'grape.core)
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-nil'>nil</span>","value":"nil"}
;; <=

;; **
;;; # Rules for creating the RTF graph for _requirements_
;;; 
;;; ## Rule: require!
;;; 
;;; This rule creates the head of the requirement.
;; **

;; @@
(rule 'require! ['action 'duration 'unit]
  :create (pattern
             (node 'r :label "Require" :asserts {:duration "&duration"})
             (node 'f :label "Frame" :asserts {:unit "'&unit'" :ordrel "1" :ordtot "1"})
             (node 'a :label "Action" :asserts {:name "'&action'"} :merge true)
             (edge :label "act" :src 'r :tar 'a)
             (edge :label "sub" :src 'r :tar 'f ))
   )
