;; **
;;; ##Rule: sub-frame!
;;; 
;;; This rule adds a sub-time frame to time frame with id _fid_.
;; **

;; **
;;; 
;; **

;; @@
(rule 'sub-frame! ['quantity 'unit 'kind 'fid]
   :read (pattern
            (node 'f :label "Frame" :oid "&fid"))
   :create (pattern
             (node 'f2 :label "Frame" :asserts {:unit "'&unit'" :q "&quantity" :kind "'&kind'" :ordrel "1" :ordtot  "1"})
             (edge :label "sub" :src 'f :tar 'f2))
   )

