;; **
;;; ## Rule: admin!
;;; 
;;; Each Frame has a subframe _or_ an administration instruction. This rule adds the latter to the time frame with id _fid_.
;; **

;; @@
(rule 'admin! ['min 'max 'fid]
   :read (pattern
            (node 'f :label "Frame" :oid "&fid"))

   :create (pattern
             (node 'a :label "Admin" :asserts {:min "&min" :max "&max"})
             (edge :label "sub" :src 'f :tar 'a))
   )
